function writeBox(element) {
    let box = element;
    msg.x = box.bbox[0];
    msg.y = box.bbox[1];
    msg.width = box.bbox[2];
    msg.height= box.bbox[3];
    msg.class = box["class"];
    msg.score = box["score"];
    node.send(msg);
}

