/* Function for Time Conversions */

function timeConverter(UNIX_timestamp){
  var a = new Date(UNIX_timestamp * 1000);
  var hour = a.getHours();
  var min = a.getMinutes();
  if (min < 10) {min = "0" + min;}
    else {min = min;}
  var sec = a.getSeconds();
  if (sec < 10) {sec = "0" + sec;}
    else {sec = sec;}

  var time = hour + ':' + min + ':' + sec ;
  return time;
}

